    //     appId: "1:397539225199:web:caf0e49dcc38535acba79a",
    //     measurementId: "G-FF3854F9LH"
    //   };
      
    
      // const app = initializeApp(firebaseConfig);
      // const student= getAuth()

      const changeemail=(e)=>{
        setEmail(e.target.value)
      }

      const changepswd=(e)=>{
        setPassword(e.target.value)
      }


      const submitdata=(e)=>{
        e.preventDefault()
        let obj={
            email: email,
            password:password
        }

        signInWithEmailAndPassword(auth,obj.email,obj.password)
        .then(()=>{
            alert("Logged in successfully")
            navigate("/a")
        })
        .catch(()=>{
            alert("error")
        })
      }
    return(
        <div className="container" style={{ "width": "600px", "marginLeft": "400px", "padding": "10px" }}>
      <div className="card">
        <div className="card-title">
          <h1 style={{ "fontSize": "20px", "marginLeft": "200px" }}><b>Student Form</b></h1>
        </div>
        <div className="card-body">
          <form onSubmit={submitdata}>
            
            
            <div className="mb-3">
              <label className="form-label">Email</label>
              <input type="email" className="form-control" value={email} placeholder="Enter email" onChange={changeemail} required />
            </div>
            
            <div className="mb-3">
              <label className="form-label">Password</label>
              <input type="password" className="form-control" value={password} placeholder="Enter password" onChange={changepswd} required />
            </div>
            
            
            <input type="submit" value="Submit" className="btn btn-success m-5" />
            <Link to="/a" className="btn btn-primary"> Back to Student Form</Link>
          </form>
        </div>
      </div>
    </div>

    )
}

export default Login;
