import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";

function StudentData(){
    const[data,setData]=useState([])
    const navigate = useNavigate()

    useEffect(()=>{
        fetch("http://localhost:3008/StudentDatabase")
        .then((res)=>{
            return res.json();
        })
        .then((resp)=>{
            console.log(resp)
            setData(resp)
        })
        
    },[])

    const ChangeEdit=(id)=>{
        navigate("/studentedit/"+ id)
    }

    const ChangeDelete=(id)=>{
        fetch("http://localhost:3008/StudentDatabase/"+ id,{
            method:"DELETE"
        })
        .then(()=>{
            alert("Deleted Successfully")
            window.location.reload()
        })
        .catch((err)=>{
            alert("error"+err)
        })
    }
    return(
        <div className="container">
            <div className="card">
                <div className="card-title">
                    <h1 style={{padding:"2px",marginLeft:"150px"}}>Student Database Management System</h1>
                    </div>
                <div className="card-body">
                    <Link to ="/StudentForm1" className="btn btn-success">ADD new(+)</Link><br/><br/>
                    <table className="table table-striped bg-info">
                    <thead className="bg-dark text-white">
                        <tr>
                            <th>Id</th>
                            <th>Name</th>
                            <th>City</th>
                            <th>EmailId</th>
                            <th>Roll No</th>
                            <th>Mobile</th>
                            <th>Course</th>
                            <th>DOJ</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                        <tbody>
                            {data.map((item)=>(
                                <tr key={item.id}>
                                    <td>{item.id}</td>
                                    <td>{item.Name}</td>
                                    <td>{item.City}</td>
                                    <td>{item.EmailId}</td>
                                    <td>{item.RollNO}</td>
                                    <td>{item.Mobile}</td>
                                    <td>{item.Course}</td>
                                    <td>{item.DOJ}</td>
                                    <td>
                                        <button onClick={()=>{ChangeEdit(item.id)}} className="btn btn-success">Edit</button>
                                        <button  onClick={()=>{ChangeDelete(item.id)}}className="btn btn-danger m-2">Delete</button>
                                        <button className="btn btn-success">Add </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
            
        </div>
    )
}
export default StudentData;
